package task.controller;import org.springframework.web.bind.annotation.GetMapping;import lombok.RequiredArgsConstructor;import lombok.extern.slf4j.Slf4j;import org.springframework.stereotype.Controller;@Slf4j@Controller@RequiredArgsConstructorpublic class MainController {    @GetMapping("/index")    public String index() {        return "/index";    }    @GetMapping("/send")    public String send(){        return "/form";    }    @GetMapping("/receive")    public String receive(){        return "/receive";    }}